template.declaration:
int parent = 0;
Link link;

int queue;
int sendQ;
Mote mote;

bool hasLink(){
    int i;
    for(i = parent+1; i < mote.parents; i++){
        if (mote.links[i] != EMPTY_LINK){
            return true;
        }
    }
    return false;
}

int calcParentLoad(Link link){
    // if there is no next link then current load should be used for this link
    if (!hasLink())
        return sendQ;
    else
        return sendQ * link.distribution/100;
}

Link selectNextLink(){
    int i;
    for(i = parent+1; i < mote.parents; i++){
        if (mote.links[i] != EMPTY_LINK){
            parent = i;
            return mote.links[i];
        }
    }
    return EMPTY_LINK;
}

int getReceiveQueue(){
    return queue;
}

int getBuffer(){
    return mote.load;
}

void initialize(){
    mote = deltaIoT.motes[moteId];
    parent = -1;
}

void calcSendEnergy(int numPackets){
     batteryUsage = batteryUsage + (numPackets * (SFTimes[8] * (powerConsumptionRate[link.powerSetting] / CoulombUnit)));
}


void receivePackets(int numPackets){
    if (queue < MAX_QUEUE)
    queue += numPackets;
}

void sendPackets(int numPackets){
    sendQ -= numPackets;
}

int periods = 0;
int numPackets;
int expectedPackets;
double avgEnergyConsumption, totalEnergy;

double getSendEnergy(){
    return batteryUsage;// + 140-numPackets * receptionTime * (receptionCost/CoulombUnit);
}

double getTimeSlotsEnergy(){
    return (((MAX_MOTES - 5 -1) * 40) * receptionTime * receptionCost)/CoulombUnit; // 40 is time slots here, 5 leaf motes + 1 gateway
}

double getMotesEnergyConsumption(){
    return getSendEnergy()+getTimeSlotsEnergy();
}

void recvPackets(int packets){
    numPackets += packets;
    expectedPackets += expected;
}

void reset(){
    numPackets = 0;
    expectedPackets = 0;
    batteryUsage = 0;
}

clock t;
int mId;
int moteId;

void initialize(){
    mId = 1;
}

bool hasTurn(){
    return mId != MAX_MOTES+1;
}

int nextTurn(){
   return topology[mId++]; 
}
