bool packetExists(Packet &packet){
    bool found = false;
    int i;
    while(i < size && found == false){
        found = packets[i++] == packet;
    }
    return found;
}

void receivePacket(Packet &packet){
    if (!packetExists(packet)){
        packets[size++] = packet;
    }
}

void receivePackets(List &recvPackets){
    int i;
    Packet packet;
    for (i = 0; i < recvPackets.size; i++){
        packet = recvPackets.packets[i];
        if (!packetExists(packet)){
            packets[size++] = packet;
        }
    }
    recvPackets.size = 0;
}

void resetQueue(){
    totalPackets = totalPackets + size;
    size = 0;
